import OptList from "./ListOpt.vue";
import AddOpt from "./AddOpt.vue";
import OptMonth from "./OptMonth.vue";
import AffGraph from "./AffGraph.vue";
import AccountManage from "./AccountsManag.vue";
import Testy from "../test.js";
export default {
  name: "CompteManagement",
  components: {
    AddOpt: AddOpt,
    OptList: OptList,
    OptMonth: OptMonth,
    AffGraph: AffGraph,
    AccountManage: AccountManage
  },
  data: function data() {
    return {
      tabOpt: [{
        title: "",
        category: "",
        Date_Operation: "",
        money: ""
      }],
      monthOpt: [{
        id: "",
        title: "",
        money: ""
      }],
      account: [{
        Operations: "",
        ES: "",
        Ratio: ""
      }]
    };
  },
  beforeCreate: function beforeCreate() {
    var _this = this;

    Testy.affOpt().then(function (response) {
      _this.tabOpt = response.data;
    });
    Testy.getTotal().then(function (response) {//this.tabOpt = response.data;
      //this.tabOpt.getTotal();
    });
  },
  methods: {
    addOpt: function addOpt(data) {
      var _this2 = this;

      //argument envoyé par l'event
      Testy.addOpt(data.title, data.category, data.Date_Operation, data.money);
      Testy.affOpt().then(function (response) {
        _this2.tabOpt.push(response.data);
      });
    },
    removeOpt: function removeOpt(id) {
      var _this3 = this;

      Testy.removeOpt(id).then(function () {
        _this3.tabOpt.forEach(function (el, index) {
          if (el.id === id) {
            _this3.tabOpt.splice(index, 1);
          }
        });
      });
    }
  }
};
